import{_ as e,o as t,c as r,a}from"./app.a012c674.js";const f=JSON.parse('{"title":"Competitors","description":"","frontmatter":{},"headers":[{"level":2,"title":"Bonfida","slug":"bonfida","link":"#bonfida","children":[]},{"level":2,"title":"ENS - Ethereum Name Service","slug":"ens-ethereum-name-service","link":"#ens-ethereum-name-service","children":[]},{"level":2,"title":"Why choose Suri?","slug":"why-choose-suri","link":"#why-choose-suri","children":[]}],"relativePath":"en/competitors.md"}'),n={name:"en/competitors.md"},o=a('<h1 id="competitors" tabindex="-1">Competitors <a class="header-anchor" href="#competitors" aria-hidden="true">#</a></h1><p>Below is a list of <strong>Suri</strong>&#39;s acknowledged competitors.</p><h2 id="bonfida" tabindex="-1">Bonfida <a class="header-anchor" href="#bonfida" aria-hidden="true">#</a></h2><p><a href="https://naming.bonfida.org/" target="_blank" rel="noreferrer">Bonfida</a> is considered <strong>Suri</strong>&#39;s main competitor. It is also based on Solana&#39;s blockchain, but it is more limited in its capabilities and development-ability, as <strong>Bonfida</strong> is not currently going beyond the current Web2&#39;s <a href="https://en.wikipedia.org/wiki/Domain_Name_System" target="_blank" rel="noreferrer">DNS</a> features in the way the Web3 deserves.</p><p>The main similarities and differences are:</p><table><thead><tr><th></th><th style="text-align:center;">Bonfida</th><th style="text-align:center;">Suri</th></tr></thead><tbody><tr><td>Blockchain</td><td style="text-align:center;">Solana</td><td style="text-align:center;">Solana</td></tr><tr><td>Domains</td><td style="text-align:center;">Only <code>.sol</code></td><td style="text-align:center;">Any level including <a href="https://en.wikipedia.org/wiki/Top-level_domain" target="_blank" rel="noreferrer">TLDs</a></td></tr><tr><td>Acquiring</td><td style="text-align:center;">Auctions</td><td style="text-align:center;"><a href="/en/protocol/subscription">Configurable Subscription model</a></td></tr><tr><td>NFT Support</td><td style="text-align:center;"><a href="https://docs.bonfida.org/collection/how-to-create-a-solana-domain-name/selling-a-domain-name/nft-domain-resell" target="_blank" rel="noreferrer">Simple</a></td><td style="text-align:center;"><a href="/en/domains/nfts">Ownership model</a></td></tr><tr><td>Tagged domains <code>@myName</code></td><td style="text-align:center;">No</td><td style="text-align:center;"><a href="/en/domains/tagged">Yes</a></td></tr><tr><td>Custom protocol support</td><td style="text-align:center;"><a href="https://bonfida.github.io/solana-name-service-guide/domain-name/records.html" target="_blank" rel="noreferrer">Limited</a></td><td style="text-align:center;"><a href="/en/protocol/records/protocols/">Fully support</a></td></tr></tbody></table><h2 id="ens-ethereum-name-service" tabindex="-1">ENS - Ethereum Name Service <a class="header-anchor" href="#ens-ethereum-name-service" aria-hidden="true">#</a></h2><p><a href="https://ens.domains/" target="_blank" rel="noreferrer">ENS</a> is a decentralized naming system based on the Ethereum blockchain. It is famous for providing access to <code>.eth</code> domains. The main advantage of ENS is to be based on ethereum, one of the most used blockchains, but that comes along with its main drawback, any action on your domain or records implies to pay a great fee.</p><p>The main similarities and differences are:</p><table><thead><tr><th></th><th style="text-align:center;">ENS</th><th style="text-align:center;">Suri</th></tr></thead><tbody><tr><td>Blockchain</td><td style="text-align:center;">Ethereum</td><td style="text-align:center;">Solana</td></tr><tr><td>Interaction fees</td><td style="text-align:center;">High</td><td style="text-align:center;">Negligible</td></tr><tr><td>Domains</td><td style="text-align:center;">Natively <code>.eth</code> and some others based on DNS</td><td style="text-align:center;">Any level including <a href="https://en.wikipedia.org/wiki/Top-level_domain" target="_blank" rel="noreferrer">TLDs</a></td></tr><tr><td>Acquiring</td><td style="text-align:center;">Single payment</td><td style="text-align:center;"><a href="/en/protocol/subscription">Configurable Subscription model</a></td></tr><tr><td>NFT Support</td><td style="text-align:center;"><a href="https://docs.ens.domains/dapp-developer-guide/ens-as-nft" target="_blank" rel="noreferrer">Only <code>*.eth</code> natively</a></td><td style="text-align:center;"><a href="/en/domains/nfts">Ownership model</a></td></tr><tr><td>Tagged domains <code>@myName</code></td><td style="text-align:center;">No</td><td style="text-align:center;"><a href="/en/domains/tagged">Yes</a></td></tr><tr><td>Custom protocol support</td><td style="text-align:center;"><a href="https://docs.ens.domains/dapp-developer-guide/managing-names" target="_blank" rel="noreferrer">Limited</a></td><td style="text-align:center;"><a href="/en/protocol/records/protocols/">Fully support</a></td></tr></tbody></table><h2 id="why-choose-suri" tabindex="-1">Why choose Suri? <a class="header-anchor" href="#why-choose-suri" aria-hidden="true">#</a></h2><p>Instead, <strong>Suri</strong> is a Web 3.0 native project and can approach the entire scope and develop the use cases needed to become the <em>foundation</em> for the next generation of internet infrastructure.</p><p>You don&#39;t believe us? <a href="/en/use-cases">See for yourself!</a></p>',13),i=[o];function d(s,l,c,h,m,p){return t(),r("div",null,i)}const u=e(n,[["render",d]]);export{f as __pageData,u as default};
